CREATE OR REPLACE FUNCTION obter_vl_unit_rop_preco ( nr_seq_roupa_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_roupas_w	bigint;
vl_preco_w	double precision;

BEGIN

begin
	select	x.vl_preco
	into STRICT	vl_preco_w
	from (
		SELECT	c.vl_preco
		from	rop_lote_roupa a,
			rop_roupa b,
			rop_lote_roupa_preco c
		where	a.nr_sequencia = b.nr_seq_lote_roupa
		and	a.nr_sequencia = c.nr_seq_lote_roupa
		and	b.nr_sequencia = nr_seq_roupa_p
		and	dt_vigencia_p between trunc(c.dt_inicio_vigencia) and fim_dia(c.dt_fim_vigencia)
		order by
			c.dt_inicio_vigencia desc,
			c.nr_sequencia desc) x LIMIT 1;

exception
when others then
	vl_preco_w := 0;
end;

return	vl_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
