SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_volume_agua ( nr_seq_producao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


QT_AGUA_W	double precision := null;

nr_seq_solubilidade_w numeric(20);

QT_VOLUME_PRESCRITO_W numeric(20);

qt_vol_agua_w numeric(20);

QT_VOLUME_TOTAL_W numeric(20);


BEGIN

	SELECT	p.nr_seq_solubilidade,
			b.qt_dose
	into STRICT nr_seq_solubilidade_w,
	QT_VOLUME_PRESCRITO_W
	FROM	prescr_medica a,
			prescr_material b,
			nut_prod_lac_item d,
			prescr_mat_hor e,
			nut_prod_principal p
	WHERE b.nr_prescricao = d.nr_prescricao
	AND	b.nr_sequencia = d.nr_seq_material
	AND	d.nr_seq_mat_hor = e.nr_sequencia
	AND	d.nr_seq_prod_princ = nr_seq_producao_p
	AND d.nr_sequencia = nr_sequencia_p
	AND d.nr_seq_prod_princ = p.nr_sequencia 
	AND	a.nr_prescricao = b.nr_prescricao;

	if (nr_seq_solubilidade_w IS NOT NULL AND nr_seq_solubilidade_w::text <> '') then

		SELECT	QT_VOL_AGUA,
				QT_VOLUME_TOTAL
		into STRICT	QT_VOL_AGUA_W,
				QT_VOLUME_TOTAL_W
		FROM	NUTRICAO_LEITE_DERIV_SOLUB
		WHERE nr_sequencia = nr_seq_solubilidade_w;

	
		if (QT_VOLUME_TOTAL_W IS NOT NULL AND QT_VOLUME_TOTAL_W::text <> '')then
			QT_AGUA_W := (QT_VOLUME_PRESCRITO_W * QT_VOL_AGUA_W)/QT_VOLUME_TOTAL_W;
		end if;
		

end if;

return	QT_AGUA_W;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
