SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_volume_consumo ( cd_material_p bigint, cd_unid_med_p text, qt_dose_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision;
cd_unid_med_w		varchar(30);
qt_conversao_w		double precision;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30) cd_unidade_medida_consumo
into STRICT	cd_unid_med_w
from	material
where	cd_material = cd_material_p;

if (cd_unid_med_p <> cd_unid_med_w) then
	select	coalesce(max(qt_conversao),1)
	into STRICT	qt_conversao_w
	from	material_conversao_unidade
	where	cd_material = cd_material_p
	and	cd_unidade_medida = cd_unid_med_p;

	qt_retorno_w := dividir(qt_dose_p,qt_conversao_w);

else
	qt_retorno_w := qt_dose_p;
end if;

return qt_retorno_w;

End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
