SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_volume_fase_nut_pac (nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_w	double precision;
qt_fase_atual_w	bigint := 0;
qt_fase_npt_w	nut_pac.qt_fase_npt%type;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select max(qt_fase_npt)
	into STRICT       qt_fase_npt_w
	from      nut_pac
	where   nr_prescricao = nr_prescricao_p
	and      nr_sequencia  = nr_sequencia_p;

	qt_fase_atual_w :=  coalesce(Obter_Etapa_Atual_NPT_prot(nr_prescricao_p,nr_sequencia_p),0);

	if (qt_fase_atual_w > qt_fase_npt_w) then
		qt_fase_atual_w  := qt_fase_atual_w - qt_fase_npt_w;
		if (qt_fase_atual_w > qt_fase_npt_w) then
			qt_fase_atual_w  := qt_fase_atual_w - qt_fase_npt_w;
		end if;
	end if;

	if (qt_fase_atual_w = 0) or (qt_fase_atual_w = 1) then
		select	sum(coalesce(qt_vol_1_fase,0))
		into STRICT	qt_volume_w
		from	nut_pac_elem_mat
		where	nr_seq_nut_pac = nr_sequencia_p;
	elsif (qt_fase_atual_w = 2) then
		select	sum(coalesce(qt_vol_2_fase,0))
		into STRICT	qt_volume_w
		from	nut_pac_elem_mat
		where	nr_seq_nut_pac = nr_sequencia_p;
	elsif (qt_fase_atual_w = 3) then
		select	sum(coalesce(qt_vol_3_fase,0))
		into STRICT	qt_volume_w
		from	nut_pac_elem_mat
		where	nr_seq_nut_pac = nr_sequencia_p;
	else
		select	sum(coalesce(qt_vol_4_fase,0))
		into STRICT	qt_volume_w
		from	nut_pac_elem_mat
		where	nr_seq_nut_pac = nr_sequencia_p;
	end if;
end if;

return qt_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
