CREATE OR REPLACE FUNCTION obter_volume_hemocomponente ( nr_sequencia_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


qt_volume_w	double precision;
ds_retorno	varchar(50) := '';


BEGIN

select	max(qt_volume)
into STRICT	qt_volume_w
from	san_derivado
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'D') then
	if (qt_volume_w > 0) then
    --458140
		ds_retorno := WHEB_MENSAGEM_PCK.get_texto(458140, 'qt_volume_w='||qt_volume_w);

	else
    -- 729618 = Unidade(s)
		ds_retorno := obter_desc_expressao(729618);
	end if;
else
	ds_retorno	:= to_char(qt_volume_w);
end if;

return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
