CREATE OR REPLACE FUNCTION obter_volume_parcial_sol_bi (ie_tipo_solucao_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_parcial_w	double precision;


BEGIN
if (ie_tipo_solucao_p IS NOT NULL AND ie_tipo_solucao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	if (ie_tipo_solucao_p = 1) then

		select	coalesce(sum(qt_volume_parcial),0)
		into STRICT	qt_volume_parcial_w
		from	prescr_solucao_evento
		where	nr_prescricao		= nr_prescricao_p
		and	nr_seq_solucao		= nr_seq_solucao_p
		and	ie_alteracao		= 14
		and	ie_evento_valido		= 'S';
	else
		select	coalesce(sum(qt_volume_parcial),0)
		into STRICT	qt_volume_parcial_w
		from	prescr_solucao_evento
		where	nr_prescricao		= nr_prescricao_p
		and	nr_seq_material		= nr_seq_solucao_p
		and	ie_alteracao		= 14
		and	ie_evento_valido		= 'S';
	end if;

end if;

return qt_volume_parcial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
