CREATE OR REPLACE FUNCTION obter_volume_parcial_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_tipo_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_w		double precision;
nr_seq_troca_frasco_w	bigint;


BEGIN

if (ie_tipo_solucao_p = 1) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_troca_frasco_w
	from	prescr_solucao_evento
	where	nr_prescricao				= nr_prescricao_p
	and	nr_seq_solucao				= nr_seq_solucao_p
	and	ie_tipo_solucao				= ie_tipo_solucao_p
	and	ie_evento_valido				= 'S'
	and	ie_alteracao				= 2
	and	obter_se_motivo_troca_frasco(nr_seq_motivo)	= 'S';

	if (nr_seq_troca_frasco_w > 0) then

		select	coalesce(sum(qt_volume_parcial),0)
		into STRICT	qt_volume_w
		from	prescr_solucao_evento
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_solucao	= nr_seq_solucao_p
		and	ie_tipo_solucao	= ie_tipo_solucao_p
		and	ie_evento_valido	= 'S'
		and	nr_sequencia	> nr_seq_troca_frasco_w;

	else

		select	coalesce(sum(qt_volume_parcial),0)
		into STRICT	qt_volume_w
		from	prescr_solucao_evento
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_solucao	= nr_seq_solucao_p
		and	ie_tipo_solucao	= ie_tipo_solucao_p
		and	ie_evento_valido	= 'S';

	end if;
	
	

elsif (ie_tipo_solucao_p = 2) then
	begin
	
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_troca_frasco_w
	from	prescr_solucao_evento
	where	nr_prescricao				= nr_prescricao_p
	and	nr_seq_material				= nr_seq_solucao_p
	and	ie_tipo_solucao				= ie_tipo_solucao_p
	and	ie_evento_valido				= 'S'
	and	ie_alteracao				= 2
	and	obter_se_motivo_troca_frasco(nr_seq_motivo)	= 'S';

	if (nr_seq_troca_frasco_w > 0) then

		select	coalesce(sum(qt_volume_parcial),0)
		into STRICT	qt_volume_w
		from	prescr_solucao_evento
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_material	= nr_seq_solucao_p
		and	ie_tipo_solucao	= ie_tipo_solucao_p
		and	ie_evento_valido	= 'S'
		and	nr_sequencia	> nr_seq_troca_frasco_w;

	else

		select	coalesce(sum(qt_volume_parcial),0)
		into STRICT	qt_volume_w
		from	prescr_solucao_evento
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_material	= nr_seq_solucao_p
		and	ie_tipo_solucao	= ie_tipo_solucao_p
		and	ie_evento_valido	= 'S';

	end if;
	
	end;
	
elsif (ie_tipo_solucao_p = 3) then

	if (coalesce(nr_seq_solucao_p,0) > 0) then	

		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_troca_frasco_w
		from	prescr_solucao_evento
		where	nr_prescricao				= nr_prescricao_p
		and	ie_tipo_solucao				=   ie_tipo_solucao_p
		and	ie_evento_valido				= 'S'
		and nr_seq_procedimento		=		nr_seq_solucao_p
		and	obter_se_motivo_troca_frasco(nr_seq_motivo)	= 'S';

		if (nr_seq_troca_frasco_w > 0) then

			select	coalesce(sum(qt_volume_parcial),0)
			into STRICT	qt_volume_w
			from	prescr_solucao_evento
			where	nr_prescricao	= nr_prescricao_p
			and	ie_tipo_solucao	= ie_tipo_solucao_p
			and	ie_evento_valido	= 'S'
			and nr_seq_procedimento	= nr_seq_solucao_p
			and	nr_sequencia	> nr_seq_troca_frasco_w;

		else

			select	coalesce(sum(qt_volume_parcial),0)
			into STRICT	qt_volume_w
			from	prescr_solucao_evento
			where	nr_prescricao	= nr_prescricao_p
			and	ie_tipo_solucao	= ie_tipo_solucao_p
			and nr_seq_procedimento	= nr_seq_solucao_p
			and	ie_evento_valido	= 'S';
		end if;

	else

		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_troca_frasco_w
		from	prescr_solucao_evento
		where	nr_prescricao				= nr_prescricao_p
		and	ie_tipo_solucao				=   ie_tipo_solucao_p
		and	ie_evento_valido				= 'S'
		and	obter_se_motivo_troca_frasco(nr_seq_motivo)	= 'S';

		if (nr_seq_troca_frasco_w > 0) then

			select	coalesce(sum(qt_volume_parcial),0)
			into STRICT	qt_volume_w
			from	prescr_solucao_evento
			where	nr_prescricao	= nr_prescricao_p
			and	ie_tipo_solucao	= ie_tipo_solucao_p
			and	ie_evento_valido	= 'S'
			and	nr_sequencia	> nr_seq_troca_frasco_w;

		else

			select	coalesce(sum(qt_volume_parcial),0)
			into STRICT	qt_volume_w
			from	prescr_solucao_evento
			where	nr_prescricao	= nr_prescricao_p
			and	ie_tipo_solucao	= ie_tipo_solucao_p
			and	ie_evento_valido	= 'S';
		end if;	
		
	end if;
	
else

	qt_volume_w := 0;

end if;

return qt_volume_w;

end;
$body$
