CREATE OR REPLACE FUNCTION obter_volume_total_leite_hor ( nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


qt_retorno_w	varchar(10);


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	select	coalesce(max(b.qt_volume_total),0) qt_volume_total
	into STRICT	qt_retorno_w
	from	prescr_leite_deriv b,
		prescr_material x,
		prescr_mat_hor c
	where	x.nr_prescricao	= c.nr_prescricao
	and	x.nr_sequencia	= c.nr_seq_material
	and	x.nr_seq_leite_deriv	= b.nr_sequencia
	and	x.nr_prescricao	= b.nr_prescricao
	and	c.nr_sequencia = nr_seq_horario_p;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
