CREATE OR REPLACE FUNCTION obter_volume_total_sne (cd_intervalo_p text, qt_dose_p bigint, nr_ocorrencia_p bigint) RETURNS bigint AS $body$
DECLARE


ie_prescricao_dieta_w	varchar(1);

BEGIN

Select	ie_prescricao_dieta
into STRICT	ie_prescricao_dieta_w
from	intervalo_prescricao
where	cd_intervalo = cd_intervalo_p;

if (ie_prescricao_dieta_w in ('I', 'H')) then
	return	qt_dose_p * nr_ocorrencia_p;
else
	return	qt_dose_p;
end if;

end;
$body$
