CREATE OR REPLACE FUNCTION obter_vol_diluente_medicamento (nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE



qt_retorno_w		double precision;
cd_unidade_medida_w	varchar(30);
cd_material_w		integer;
qt_dose_w		double precision;


BEGIN

select	cd_material,
	coalesce(qt_solucao,qt_dose),
	cd_unidade_medida_dose
into STRICT	cd_material_w,
	qt_dose_w,
	cd_unidade_medida_w
from	prescr_material
where	nr_prescricao			= nr_prescricao_p
and		nr_sequencia_diluicao	= nr_sequencia_p
and		ie_agrupador			= 3;

if (cd_unidade_medida_w = OBTER_UNID_MED_USUA('ml')) then
	qt_retorno_w	:= qt_dose_w;
else
	select	obter_conversao_ml(cd_material_w,qt_dose_w,cd_unidade_medida_w)
	into STRICT	qt_retorno_w
	;
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
