CREATE OR REPLACE FUNCTION obter_vol_elem_nut_pac_ml ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE



qt_volume_w		double precision := 0;


BEGIN

select	coalesce(sum(coalesce(a.qt_volume,0) * coalesce(b.qt_conversao_ml,1)),0)
into STRICT	qt_volume_w
from	nut_elem_material b,
	nut_pac_elem_mat a
where	a.nr_seq_pac_elem	= nr_sequencia_p
and	a.nr_seq_elem_mat	= b.nr_sequencia
and	coalesce(b.ie_tipo,'NPT')	= 'NPT';

return qt_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
