CREATE OR REPLACE FUNCTION obter_vol_elem_nut_pac ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE



qt_volume_w			double precision := 0;
ie_arredonda_npt_w	parametro_medico.ie_arredondar_npt%type;


BEGIN

select 	coalesce(max(ie_arredondar_npt),'N')
into STRICT	ie_arredonda_npt_w
from	parametro_medico
where 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

select	coalesce(sum(coalesce(qt_volume,0)),0)
into STRICT	qt_volume_w
from	nut_pac_elem_mat
where	nr_seq_pac_elem	= nr_sequencia_p;

if (ie_arredonda_npt_w = 'S') then
	--return ceil(qt_volume_w * 100) / 100;
	return round((qt_volume_w)::numeric, 2);
else
	return qt_volume_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
