CREATE OR REPLACE FUNCTION obter_vol_etapas_alternadas ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
qt_volume_w		double precision;
nr_etapa_w		integer;

C01 CURSOR FOR
	SELECT	qt_volume,
		row_number() OVER () AS rownum
	from	prescr_solucao_esquema
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_solucao = nr_seq_solucao_p
	order by nr_sequencia;


BEGIN
ds_retorno_w := '';

open C01;
loop
fetch C01 into
	qt_volume_w,
	nr_etapa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w := ds_retorno_w || obter_desc_expressao(289624)/*'Etapa: '*/
||': '||to_char(nr_etapa_w)||obter_desc_expressao(302238)/*' Volume: '*/||': '||qt_volume_w||chr(10);

	end;
end loop;
close C01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
