CREATE OR REPLACE FUNCTION obter_zona_atendimento (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_procedencia_w    integer;
ie_zona_procedencia_w  smallint;


BEGIN 
 
select max(cd_procedencia) 
into STRICT  cd_procedencia_w 
from  atendimento_paciente 
where nr_atendimento = nr_atendimento_p;
 
if (cd_procedencia_w IS NOT NULL AND cd_procedencia_w::text <> '') then 
    select max(ie_zona_procedencia) 
    into STRICT  ie_zona_procedencia_w 
    from  procedencia 
    where cd_procedencia = cd_procedencia_w 
    and  ie_situacao = 'A';
end if;
 
return ie_zona_procedencia_w;
 
end;
