SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obt_cpoe_stand_cmt (ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(8000);
ds_comment_w			varchar(8000);

c01 CURSOR FOR
	SELECT	ds_comment
	from 	cpoe_std_comment
	where 	si_validation_type = 2;


BEGIN

if (ie_opcao_p = 'B')	then
	open c01;
	loop
	fetch c01 into 
		ds_comment_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_retorno_w	:= ds_retorno_w || ds_comment_w || ',';
		end;
	end loop;
	close c01;
end if;

return	SUBSTR(ds_retorno_w, 1, LENGTH(ds_retorno_w) - 1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
