CREATE OR REPLACE FUNCTION obt_periodo_turno (dt_entrada_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_periodo_turno_w	varchar(100);


BEGIN
if (dt_entrada_p IS NOT NULL AND dt_entrada_p::text <> '') then

	select	max(ds)
	into STRICT	ds_periodo_turno_w
	from (SELECT	'07:01 as 13:00' ds
		
		where	dt_entrada_p between trunc(dt_entrada_p)+07.000166/24 and trunc(dt_entrada_p)+13/24
		
union

		SELECT	'13:01 as 19:00' ds
		
		where	dt_entrada_p between trunc(dt_entrada_p)+13.000166/24 and trunc(dt_entrada_p)+19/24
		
union

		select	'19:01 as 00:00' ds
		
		where	dt_entrada_p between trunc(dt_entrada_p)+19.000166/24 and trunc(dt_entrada_p)+23.9998/24
		or	dt_entrada_p between trunc(dt_entrada_p-1)+24/24 and trunc(dt_entrada_p-1)+24/24
		
union

		select	'00:01 as 07:00' ds
		
		where	dt_entrada_p between trunc(dt_entrada_p-1)+24.000166/24 and trunc(dt_entrada_p)+7/24) alias12;

end if;

return	ds_periodo_turno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
