CREATE OR REPLACE FUNCTION ocultar_paciente_painel_agenda ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint) RETURNS varchar AS $body$
DECLARE


qt_evento_oculta_pac_w bigint;
ie_ocultar_pac_painel_w varchar(1) := 'N';

/* 'S'  -  Ocultar paciente no painel*/

BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') or (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then
	select count(*)
	into STRICT   qt_evento_oculta_pac_w
	from   evento_cirurgia a,
	       evento_cirurgia_paciente b
	where  a.nr_sequencia = b.nr_seq_evento
	and    ((b.nr_cirurgia = nr_cirurgia_p ) or (b.nr_seq_pepo = nr_seq_pepo_p ))
	and (coalesce(a.ie_ocultar_paciente,'N') = 'S')
	and     coalesce(b.ie_situacao,'A') = 'A';

end if;

if (qt_evento_oculta_pac_w > 0) then
	ie_ocultar_pac_painel_w := 'S';
else
	ie_ocultar_pac_painel_w := 'N';
end if;


return	ie_ocultar_pac_painel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
