CREATE OR REPLACE FUNCTION odonto_obter_faces (cd_faces_p text) RETURNS varchar AS $body$
DECLARE


arr_faces varchar(255);
nr_posicao_str_w	bigint;
nr_seq_face_w   	varchar(100);
nr_seq_faces_w		varchar(255);
nr_seq_face_p		varchar(255);


BEGIN
    nr_seq_faces_w := cd_faces_p;

while((nr_seq_faces_w IS NOT NULL AND nr_seq_faces_w::text <> '') or trim(both nr_seq_faces_w) <> '' ) loop

			nr_posicao_str_w := position(', ' in nr_seq_faces_w);

			if ( nr_posicao_str_w = 0 ) then
				nr_seq_face_w := trim(both nr_seq_faces_w);
				nr_seq_faces_w := null;
			else
				nr_seq_face_w := substr(nr_seq_faces_w, 1, nr_posicao_str_w - 1);
				nr_seq_faces_w := substr(nr_seq_faces_w, nr_posicao_str_w + 2);
			end if;
			
			select  max(nr_seq_face) nr_seq_face
			into STRICT    nr_seq_face_w
			from	area_imagem_item
			where	nr_seq_area = nr_seq_face_w;

            arr_faces := arr_faces|| nr_seq_face_w||' ';
        end loop;

    return trim(both arr_faces);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
