SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION odont_obter_se_altera_status ( nr_seq_odont_proced_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'N';
ie_status_w		varchar(3);
qt_tempo_w		bigint;


BEGIN

if (nr_seq_odont_proced_p IS NOT NULL AND nr_seq_odont_proced_p::text <> '') then

	select	max(ie_status)
	into STRICT	ie_status_w
	from	odont_historico
	where	nr_seq_odont_proced = nr_seq_odont_proced_p
	and	nr_sequencia = (SELECT	max(nr_sequencia)
				from	odont_historico
				where	nr_seq_odont_proced = nr_seq_odont_proced_p);

	if (ie_status_w = 'I') then

		ds_retorno_w := 'N';

	elsif (ie_status_w = 'P') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	odont_historico
		where	nr_seq_odont_proced = nr_seq_odont_proced_p
		and	exists (SELECT	1
				from	odont_historico
				where	nr_seq_odont_proced = nr_seq_odont_proced_p
				and	ie_status = 'I');

	else

		select	CASE WHEN count(*)=1 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	odont_historico
		where	nr_seq_odont_proced = nr_seq_odont_proced_p;

	end if;


end if;

if ( ds_retorno_w = 'S') then

	qt_tempo_w := obter_param_usuario(281, 1236, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, qt_tempo_w);


	Select 	odont_obter_se_desfaz_status(nr_seq_odont_proced_p,qt_tempo_w)
	into STRICT	ds_retorno_w
	;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
