CREATE OR REPLACE FUNCTION odont_obter_se_desfaz_status ( nr_seq_odont_proced_p bigint, qt_tempo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
nr_seq_odont_historico_w	bigint;
qt_tempo_w			bigint;


BEGIN
ds_retorno_w	:= 'N';
if (qt_tempo_p = 0) then
	ds_retorno_w := 'S';
end if;
if (nr_seq_odont_proced_p IS NOT NULL AND nr_seq_odont_proced_p::text <> '') and (qt_tempo_p > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_seq_odont_historico_w
	from	odont_historico
	where	nr_seq_odont_proced = nr_seq_odont_proced_p
	and	dt_historico = (SELECT	max(dt_historico)
				from	odont_historico
				where	nr_seq_odont_proced = nr_seq_odont_proced_p);

	if (nr_seq_odont_historico_w IS NOT NULL AND nr_seq_odont_historico_w::text <> '') then

		select	obter_minutos_hora(obter_dif_data(dt_historico,clock_timestamp(),'F'))
		into STRICT	qt_tempo_w
		from	odont_historico
		where	nr_sequencia = nr_seq_odont_historico_w;

		if (qt_tempo_w <= qt_tempo_p) then
			ds_retorno_w := 'S';
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
