CREATE OR REPLACE FUNCTION odont_obter_se_status_alterado (nr_seq_odont_proced_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);
ie_status_w	varchar(3);

BEGIN
ds_retorno_w := 'N';
if (nr_seq_odont_proced_p IS NOT NULL AND nr_seq_odont_proced_p::text <> '') then
	select	ie_status
	into STRICT	ie_status_w
	from	odont_historico
	where	nr_seq_odont_proced = nr_seq_odont_proced_p
	and	nr_sequencia = (SELECT	max(nr_sequencia)
				from	odont_historico
				where	nr_seq_odont_proced = nr_seq_odont_proced_p);

	if (ie_status_w = 'I') then
		ds_retorno_w := 'N';
	elsif (ie_status_w = 'P') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	odont_historico
		where	nr_seq_odont_proced = nr_seq_odont_proced_p
		and	exists (SELECT	1
				from	odont_historico
				where	nr_seq_odont_proced = nr_seq_odont_proced_p
				and	ie_status = 'I');
	else
		select	CASE WHEN count(*)=1 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	odont_historico
		where	nr_seq_odont_proced = nr_seq_odont_proced_p;
	end if;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
