CREATE OR REPLACE FUNCTION odt_obter_se_espec_medico ( cd_especialidade_p bigint, cd_profissional_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

if (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	especialidade_prof_odont
	where	cd_pessoa_fisica = cd_profissional_p
	and	cd_especialidade = cd_especialidade_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
