CREATE OR REPLACE FUNCTION oft_obter_formulario ( nr_seq_tipo_consulta_p bigint) RETURNS bigint AS $body$
DECLARE

	

nr_sequencia_w			oft_formulario_regra.nr_sequencia%type;
nr_sequencia_ww		oft_formulario_regra.nr_sequencia%type;
cd_perfil_w				perfil.cd_perfil%type	:= wheb_usuario_pck.get_cd_perfil;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type	:= wheb_usuario_pck.get_cd_estabelecimento;
	
c01 CURSOR FOR							
	SELECT 	b.nr_sequencia
	from   	oft_formulario a,
				oft_formulario_regra b
	where  	a.nr_sequencia 		   								   = b.nr_seq_formulario
	and	   coalesce(a.nr_seq_tipo_consulta,nr_seq_tipo_consulta_p) = nr_seq_tipo_consulta_p
	and	   coalesce(b.cd_perfil,cd_perfil_w) 							   = cd_perfil_w
	and	   coalesce(b.cd_estabelecimento,cd_estabelecimento_w) 	   = cd_estabelecimento_w
	and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and		coalesce(a.ie_situacao,'A') = 'A'
	and		coalesce(b.ie_situacao,'A') = 'A'
	order by coalesce(b.cd_perfil,0),
				coalesce(b.cd_estabelecimento,0);


BEGIN

open C01;
loop
fetch C01 into	
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_sequencia_ww := nr_sequencia_w;
	end;
end loop;
close C01;
	
return nr_sequencia_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
