CREATE OR REPLACE FUNCTION oft_obter_tonometria_pneu ( nr_seq_consulta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

						  
ie_busca_w		varchar(1);
cd_perfil_w		integer;
nm_usuario_w		varchar(15);
cd_estabelecimento_w	integer;
vl_pressao_od_w		real;
vl_pressao_oe_w		real;
ds_observacao_w		varchar(255);
vl_pressao_od_ww	real;
vl_pressao_oe_ww	real;
ds_observacao_ww	varchar(255);
cd_pessoa_fisica_w	varchar(10);
nr_atendimento_w	bigint;

ds_retorno_w		varchar(2000) := null;

c01 CURSOR FOR 
	SELECT	a.vl_pressao_od, 
		a.vl_pressao_oe, 
		a.ds_observacao 
	from 	oft_tonometria a, 
		oft_consulta b 
	where	a.nr_seq_consulta 	= b.nr_sequencia 
	and	((ie_busca_w = 'C' AND b.nr_sequencia 	= nr_seq_consulta_p) or 
		 (ie_busca_w = 'P' AND b.cd_pessoa_fisica 	= cd_pessoa_fisica_w)) 
	and	a.ie_tipo_tonometria 	= 1 
	and	coalesce(a.ie_situacao,'A') 	= 'A' 
	order by a.dt_exame;
	

BEGIN 
 
cd_perfil_w		:= wheb_usuario_pck.get_cd_perfil;
nm_usuario_w		:= wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
 
ie_busca_w := Obter_Param_Usuario(3010, 118, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_busca_w);
 
if (ie_busca_w <> 'N') then 
	if (ie_busca_w = 'P') then 
		select	max(cd_pessoa_fisica), 
			max(nr_atendimento) 
		into STRICT	cd_pessoa_fisica_w, 
			nr_atendimento_w 
		from	oft_consulta 
		where	nr_sequencia = nr_seq_consulta_p;
		if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
			select	max(cd_pessoa_fisica) 
			into STRICT	cd_pessoa_fisica_w 
			from	atendimento_paciente 
			where	nr_atendimento = nr_atendimento_w;
		end if;
	end if;	
		 
	open C01;
	loop 
	fetch C01 into	 
		vl_pressao_od_w, 
		vl_pressao_oe_w, 
		ds_observacao_w;	
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		vl_pressao_od_ww := vl_pressao_od_w;
		vl_pressao_oe_ww := vl_pressao_oe_w;
		ds_observacao_ww := ds_observacao_w;
		end;
	end loop;
	close C01;
end if;	
	 
if (ie_opcao_p = 'VLPOD') then	 
	ds_retorno_w	:= vl_pressao_od_ww;
elsif (ie_opcao_p = 'VLPOE') then	 
	ds_retorno_w	:= vl_pressao_oe_ww;
elsif (ie_opcao_p = 'OBS') then	 
	ds_retorno_w	:= ds_observacao_ww;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
