CREATE OR REPLACE FUNCTION oft_obter_valor_com_sinal (vl_oft_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN
if (vl_oft_p IS NOT NULL AND vl_oft_p::text <> '') and (vl_oft_p < 1000000) then
	if (vl_oft_p > 0) then
		ds_retorno_w := '+' || to_char(vl_oft_p, 'fm999990.00');
	else
		ds_retorno_w := to_char(vl_oft_p, 'fm999990.00');
	end if;
end if;

return	ds_retorno_w;

end;
