CREATE OR REPLACE FUNCTION onco_dados_movimento_estoque ( cd_material_p bigint, dt_mesano_referencia_p timestamp, dt_movimento_estoque_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w			bigint;
nr_movimento_estoque_w		bigint;

ds_prescricao_w			varchar(255);
ds_movimento_estoque_w		varchar(255);
ds_retorno_w			varchar(255) := '';

/* ie_opcao_p
M - NR_MOVIMENTO
P  - PRESCRICAO
*/
C01 CURSOR FOR
	SELECT	a.nr_prescricao,
		a.nr_movimento_estoque
	from    	movimento_estoque a
	where   	trunc(a.dt_movimento_estoque, 'dd') = trunc(dt_movimento_estoque_p, 'dd')
	and	a.dt_mesano_referencia = dt_mesano_referencia_p
	and	a.cd_material_estoque = cd_material_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_prescricao_w,
	nr_movimento_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_opcao_p = 'P') and (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') and (obter_se_contido_char(ds_retorno_w, nr_prescricao_w) = 'N') then
		ds_retorno_w := substr(ds_retorno_w || nr_prescricao_w || ', ',1,255);
	end if;

	if (ie_opcao_p = 'M') and (nr_movimento_estoque_w IS NOT NULL AND nr_movimento_estoque_w::text <> '') and (obter_se_contido_char(ds_retorno_w, nr_movimento_estoque_w) = 'N') then
		ds_retorno_w := substr(ds_retorno_w || nr_movimento_estoque_w || ', ',1,255);
	end if;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
