CREATE OR REPLACE FUNCTION opty_obter_laudo_requisitante ( nr_seq_proc_p bigint, ie_tipo_estrutura_p bigint) RETURNS bigint AS $body$
DECLARE


qtd_w		bigint;
resultado_w	bigint := 1;


BEGIN

if (nr_seq_proc_p IS NOT NULL AND nr_seq_proc_p::text <> '') then
	begin

	select	count(p.nr_sequencia)
	into STRICT	qtd_w
	from	procedimento_paciente p,
		laudo_paciente l
	where	p.nr_sequencia = l.nr_seq_proc
	and	p.cd_medico_req = l.cd_medico_resp
	and	p.nr_sequencia = nr_seq_proc_p;

	if (qtd_w > 0) then
		resultado_w := 0;
	end if;

	end;
end if;

return resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
