CREATE OR REPLACE FUNCTION opty_obter_se_laudo_repass ( nr_seq_proc_p bigint, ie_tipo_estrutura_p bigint) RETURNS bigint AS $body$
DECLARE


--Objeto criado pelo cliente  HOB Hospital Oftalmológico de Brasilia para utilização nos repasses
qtd_w		bigint;
resultado_w 	bigint := 1;


BEGIN

if (nr_seq_proc_p IS NOT NULL AND nr_seq_proc_p::text <> '') then
	begin

	select	count(p.nr_sequencia)
	into STRICT	qtd_w
	from	procedimento_paciente p,
		laudo_paciente l
	where	p.nr_sequencia = l.nr_seq_proc
	and	l.cd_medico_resp in (518885, 530228,530233,530231,530246,516688,530200)
	and	p.nr_seq_proc_interno in (11348,11347,13444,11913,11875,11909,14481,13403,11866,11878,11866,11362)
	and	p.nr_sequencia = nr_seq_proc_p;

	if (qtd_w > 0) then
		resultado_w := 0;
	end if;

	end;
end if;

return resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
