CREATE OR REPLACE FUNCTION padronizar_nome (ds_campo_p text) RETURNS varchar AS $body$
DECLARE


/*RETORNA O NOME COM TODAS AS LETRAS EM MAISCULO E SEM ACENTO*/

ds_campo_w		varchar(255);
i				integer;


BEGIN
ds_campo_w		:= upper(ds_campo_p);
ds_campo_w		:= elimina_acentuacao(ds_campo_w);
ds_campo_w      := regexp_replace(ds_campo_w, '[[:space:]]+', ' ');

