CREATE OR REPLACE FUNCTION padroniza_horario_md ( ds_horario_p text) RETURNS varchar AS $body$
DECLARE

    ds_horarios_w		varchar(2000);
    ds_texto_w		varchar(2000);
    ds_horario_w		varchar(2000);
    type Vetor is table of campos index by integer;
    horario_w		Vetor;
    i			integer;
