SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION paf_obter_compl_pf ( cd_pessoa_fisica_p text, ie_opcao_p text, ie_tipo_complemento_p bigint) RETURNS varchar AS $body$
DECLARE


/* utilizar sql's distintos para reduzir consumo de memoria

ie_tipo_complemento_p
1 - residencial
2 - comercial
3 - responsável
4 - pai
5 - mãe
6 - conjuge
7 - comercial 2

ie_opcao_p
E - endereco
EN - enderero + numero

*/
ds_retorno_w	varchar(255);


BEGIN

begin

	if (ie_tipo_complemento_p > 0) then
		begin

		if (ie_opcao_p = 'E') then
			begin

			select	substr(ds_endereco, 1, 255)
			into STRICT	ds_retorno_w
			from (SELECT	ds_endereco
				from	compl_pessoa_fisica
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				and	ie_tipo_complemento = ie_tipo_complemento_p
				order by
					nr_sequencia desc) alias3 LIMIT 1;

			end;

		elsif (ie_opcao_p = 'EN') then
			begin

			select	substr(ds_endereco, 1, 255)
			into STRICT	ds_retorno_w
			from (SELECT	ds_endereco || CASE WHEN coalesce(nr_endereco::text, '') = '' THEN  ''  ELSE ', ' || to_char(nr_endereco) END  ds_endereco
				from	compl_pessoa_fisica
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				and	ie_tipo_complemento = ie_tipo_complemento_p
				order by
					nr_sequencia desc) alias4 LIMIT 1;

			end;
		end if;

		end;
	end if;

exception
when others then
	ds_retorno_w := '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
