CREATE OR REPLACE FUNCTION paf_requisito_ix_n1 () RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
cd_cnpj_w	varchar(20);


BEGIN

select 	max(cd_cnpj_desenvolvedora)
into STRICT	cd_cnpj_w
from	paf_parametros;

select	'N1' ||
	substr(lpad(coalesce(cd_cnpj_w,'0'),14,'0'),1,14) ||
	substr(lpad(coalesce(nr_inscricao_estadual,'0'),14,'0'),1,14)  ||
	substr(lpad(coalesce(nr_inscricao_municipal,'0'),14,'0'),1,14) ||
	substr(rpad(coalesce(ds_razao_social,' '),50,' '),1,50)
into STRICT	ds_retorno_W
from	pessoa_juridica
where	cd_cgc = cd_cnpj_w;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
