CREATE OR REPLACE FUNCTION paf_requisito_ix_n9 (qt_registros_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
cd_cnpj_w		varchar(20);
nr_inscricao_estadual_w	varchar(20);

BEGIN

select 	max(cd_cnpj_desenvolvedora)
into STRICT	cd_cnpj_w
from	paf_parametros;

select	nr_inscricao_estadual
into STRICT	nr_inscricao_estadual_w
from	pessoa_juridica
where	cd_cgc = cd_cnpj_w;

select	'N9' ||
	substr(lpad(coalesce(cd_cnpj_w,'0'),14,'0'),1,14) ||
	substr(lpad(coalesce(nr_inscricao_estadual_w,'0'),14,'0'),1,14)  ||
	substr(lpad(qt_registros_p,6,'0'),1,6)
into STRICT	ds_retorno_w
;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
