CREATE OR REPLACE FUNCTION paf_requisito_vii_item_7_estab (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN

select 	'P1' ||
	substr(lpad(elimina_caractere_especial(e.cd_cgc),14,'0'),1,14) ||
	substr(lpad(elimina_caractere_especial(coalesce(p.nr_inscricao_estadual,' ')),14,'0'),1,14) ||
	substr(lpad(elimina_caractere_especial(coalesce(p.nr_inscricao_municipal,' ')),14,'0'),1,14) ||
	substr(rpad(p.ds_razao_social,50,' '),1,50)
into STRICT	ds_retorno_w
from 	estabelecimento e,
	pessoa_juridica p
where	e.cd_cgc = p.cd_cgc
and	e.cd_estabelecimento = cd_estabelecimento_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
