CREATE OR REPLACE FUNCTION panorama_obter_exame_agendado ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(4000);
ds_retorno_ww varchar(4000);
ie_status_execucao_w	prescr_procedimento.ie_status_execucao%type;
C01 CURSOR FOR
	SELECT	substr('('||obter_desc_proc_inter_conv(a.cd_procedimento,a.ie_origem_proced,a.nr_seq_proc_interno)||' - ' || to_char(a.hr_inicio,'dd/mm hh24:mi')||')',1,255)
	from	agenda_paciente a,
		agenda c
	where	a.nr_atendimento = nr_atendimento_p
	and	a.ie_status_agenda not in ('C','E')
	and	a.cd_agenda = c.cd_agenda
	and	c.cd_tipo_agenda = 2
	order by 1;
	
C02 CURSOR FOR
	SELECT	substr('('||obter_desc_proc_inter_conv(d.cd_procedimento,d.ie_origem_proced,d.nr_seq_proc_interno)||' - ' || to_char(a.hr_inicio,'dd/mm hh24:mi')||')',1,255)
	from	agenda_paciente a,	
		agenda c,
		agenda_paciente_proc d
	where	a.nr_atendimento = nr_atendimento_p
	and	a.nr_sequencia  = d.nr_sequencia
	and	a.cd_agenda = c.cd_agenda
	and	c.cd_tipo_agenda = 2
	and	a.ie_status_agenda not in ('C','E')
	order by 1;	

BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	open C01;
	loop
	fetch C01 into	
		ds_retorno_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			ds_retorno_w := substr(ds_retorno_w || ds_retorno_ww||' ',1,4000);
		end;
	end loop;
	close C01;
	
	open C02;
	loop
	fetch C02 into	
		ds_retorno_ww;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
			ds_retorno_w := substr(ds_retorno_w || ds_retorno_ww||' ',1,4000);
		end;
	end loop;
	close C02;	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION panorama_obter_exame_agendado ( nr_atendimento_p bigint) FROM PUBLIC;

