CREATE OR REPLACE FUNCTION pan_obter_se_pac_alergia (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_alergia_w     varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_alergia_w
from    paciente_alergia
where   cd_pessoa_fisica  = cd_pessoa_fisica_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	ie_nega_alergias <> 'S'
and          coalesce(dt_inativacao::text, '') = '';

return	ie_alergia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
