CREATE OR REPLACE FUNCTION patient_belong_departament (nr_seq_interno_p unidade_atendimento.nr_seq_interno%type, cd_departamento_p departamento_medico.cd_departamento%type) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w	unidade_atendimento.nr_atendimento%type;
cd_departamento_w	departamento_medico.cd_departamento%type;
ds_retorno_w		varchar(1) := 'N';
			

BEGIN



if (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '') then
	select 	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	unidade_atendimento
	where   nr_seq_interno = nr_seq_interno_p;
	
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') 	then
		cd_departamento_w	:= obter_departamento_data(nr_atendimento_w, clock_timestamp());
	
		if (cd_departamento_w IS NOT NULL AND cd_departamento_w::text <> '') then
			if (cd_departamento_w = cd_departamento_p)	then
				ds_retorno_w := 'S';
			else
				ds_retorno_w := 'N';
			end if;
		else
			ds_retorno_w := 'N';
		end if;
	else
		ds_retorno_w  := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
