CREATE OR REPLACE FUNCTION pat_obter_baixa_bem (nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(a.dt_historico)
	into STRICT	dt_retorno_w
	from	pat_tipo_historico b,
		pat_historico_bem a
	where	a.nr_seq_tipo		= b.nr_sequencia
	and	b.ie_valor		= 'B'
	and	b.ie_transferencia	= 'N'
	and	a.nr_seq_bem		= nr_sequencia_p;

end if;

return	dt_retorno_w;

end;
