CREATE OR REPLACE FUNCTION pat_obter_centro_custo_bem ( nr_seq_bem_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_centro_custo_w		pat_bem.cd_centro_custo%type;
nr_seq_alteracao_w		pat_historico_bem.nr_sequencia%type;


BEGIN

if (coalesce(nr_seq_bem_p,0) <> 0) then
	begin

	select	cd_centro_custo
	into STRICT	cd_centro_custo_w
	from	pat_bem
	where	nr_sequencia	= nr_seq_bem_p;

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_alteracao_w
	from	pat_historico_bem a,
		pat_tipo_historico	b
	where	a.nr_seq_tipo	= b.nr_sequencia
	and	b.ie_transferencia	= 'S'
	and	a.nr_seq_bem	= nr_seq_bem_p
	and	a.dt_historico	= (	SELECT	min(y.dt_historico)
					from	pat_historico_bem y,
						pat_tipo_historico x
					where	x.nr_sequencia	= y.nr_seq_tipo
					and	b.ie_transferencia	= 'S'
					and	y.nr_seq_bem	= nr_seq_bem_p
					and	y.dt_historico >= dt_vigencia_p);

	if (coalesce(nr_seq_alteracao_w,0) <> 0) then

		select	max(cd_centro_custo_ant)
		into STRICT	cd_centro_custo_w
		from	pat_historico_bem
		where	nr_sequencia	= nr_seq_alteracao_w;

	end if;
	end;
end if;

return	cd_centro_custo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
