CREATE OR REPLACE FUNCTION pat_obter_conta_contabil_bem ( nr_seq_bem_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w		varchar(20);
cd_conta_nova_w			varchar(20);
nr_seq_alteracao_w		bigint;


BEGIN

if (coalesce(nr_seq_bem_p,0) <> 0) then
	begin

	select	cd_conta_contabil
	into STRICT	cd_conta_contabil_w
	from	pat_bem
	where	nr_sequencia	= nr_seq_bem_p;

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_alteracao_w
	from	pat_conta_alteracao a
	where	a.nr_seq_bem	= nr_seq_bem_p
	and	a.dt_transacao	= (	SELECT	min(y.dt_transacao)
					from	pat_conta_alteracao y
					where	y.nr_seq_bem	= nr_seq_bem_p
					and	y.dt_transacao >= dt_vigencia_p);

	if (coalesce(nr_seq_alteracao_w,0) <> 0) then

		select	max(cd_conta_antiga)
		into STRICT	cd_conta_contabil_w
		from	pat_conta_alteracao
		where	nr_seq_bem	= nr_seq_bem_p
		and	nr_sequencia	= nr_seq_alteracao_w;

	end if;
	end;
end if;

return	cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
