CREATE OR REPLACE FUNCTION pat_obter_dados_bem ( nr_seq_bem_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_valor_w			varchar(15);
ds_bem_w			varchar(255);
ds_retorno_w			varchar(255);
nr_seq_local_w			bigint;


/*

TV	Tipo valor
DL	Descrição da localização do bem
*/
BEGIN

if (nr_seq_bem_p IS NOT NULL AND nr_seq_bem_p::text <> '') then
	select	ie_tipo_valor,
		nr_seq_local,
		ds_bem
	into STRICT	ie_tipo_valor_w,
		nr_seq_local_w,
		ds_bem_w
	from	pat_bem
	where	nr_sequencia	= nr_seq_bem_p;

	if (ie_opcao_p = 'TV') then
		ds_retorno_w	:= substr(ie_tipo_valor_w,1,1);
	elsif (ie_opcao_p = 'DL') then
		ds_retorno_w	:= substr(pat_obter_dados_local(nr_seq_local_w, 'D'),1,255);
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_bem_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
