SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pat_obter_dados_local ( nr_seq_local_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
E	- Estabelecimento
D	- Descrição do local
R	- Pessoa Responsável

*/
cd_pf_resp_local_w		varchar(10);
ds_estabelecimento_w	varchar(100);
ds_local_w		varchar(255);
ds_retorno_w		varchar(255);



BEGIN

select	substr(max(obter_nome_estabelecimento(a.cd_estabelecimento)), 1, 100),
	substr(max(a.ds_local), 1, 255),
	max(cd_pf_resp_local)
into STRICT	ds_estabelecimento_w,
	ds_local_w,
	cd_pf_resp_local_w
from	pat_local a
where	a.nr_sequencia	= nr_seq_local_p;


if (ie_opcao_p	= 'E') then

	ds_retorno_w	:= ds_estabelecimento_w;
elsif (ie_opcao_p	= 'D') then

	ds_retorno_w	:= ds_local_w;
elsif (ie_opcao_p	= 'R') then

	ds_retorno_w	:= cd_pf_resp_local_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
