CREATE OR REPLACE FUNCTION pat_obter_desc_conta_ref (nr_seq_conta_ref_p pat_conta_contabil.nr_seq_conta_ref%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	pat_conta_contabil.ds_regra%type;

BEGIN

	select	substr(coalesce(pat_obter_desc_regra_conta(nr_sequencia), obter_desc_conta_contabil(cd_conta_contabil)), 1, 255)
	into STRICT		ds_retorno_w
	from		pat_conta_contabil
