SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pat_obter_perc_baixa_bem ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_percentual_w				double precision;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	coalesce(max(a.qt_percentual),0)
	into STRICT	qt_percentual_w
	from	pat_historico_bem a,
		pat_tipo_historico b
	where	a.nr_seq_tipo	= b.nr_sequencia
	and	b.ie_valor	in ('B','V')
	and	a.nr_seq_bem	= nr_sequencia_p
	order by a.dt_historico asc;

end if;

return	qt_percentual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
