CREATE OR REPLACE FUNCTION pat_obter_regra_conta_bem ( nr_seq_bem_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_conta_w			pat_bem.nr_seq_regra_conta%type;
nr_seq_alteracao_w			bigint;
dt_fim_mes_w				timestamp;



BEGIN

if (coalesce(nr_seq_bem_p,0) <> 0) then
	begin
	dt_fim_mes_w	:= fim_mes(dt_vigencia_p);

	select	nr_seq_regra_conta
	into STRICT	nr_seq_regra_conta_w
	from	pat_bem
	where	nr_sequencia	= nr_seq_bem_p;

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_alteracao_w
	from	pat_conta_alteracao a
	where	a.nr_seq_bem	= nr_seq_bem_p
	and	a.dt_transacao	= (	SELECT	max(y.dt_transacao)
					from	pat_conta_alteracao y
					where	y.nr_seq_bem	= nr_seq_bem_p
					and	y.dt_transacao <= dt_fim_mes_w);

	if (coalesce(nr_seq_alteracao_w,0) <> 0) then
		select	max(nr_seq_regra_conta_nova)
		into STRICT	nr_seq_regra_conta_w
		from	pat_conta_alteracao
		where	nr_seq_bem	= nr_seq_bem_p
		and	nr_sequencia	= nr_seq_alteracao_w;

	end if;
	end;
end if;

return	nr_seq_regra_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
