CREATE OR REPLACE FUNCTION pat_obter_se_bem_usuario ( nr_seq_bem_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'S';
cd_centro_custo_w			bigint;
cd_estabelecimento_w		bigint;
nr_seq_crit_rateio_w		bigint;


BEGIN

select	coalesce(max(nr_seq_crit_rateio),0),
	max(cd_centro_custo),
	max(cd_estabelecimento)
into STRICT	nr_seq_crit_rateio_w,
	cd_centro_custo_w,
	cd_estabelecimento_w
from	pat_bem
where	nr_sequencia	= nr_seq_bem_p;

if (nr_seq_crit_rateio_w = 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	setor_atendimento b,
		usuario_setor_v a
	where	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	b.cd_estabelecimento_base	= cd_estabelecimento_w
	and	a.nm_usuario		= nm_usuario_p
	and	b.cd_centro_custo		= cd_centro_custo_w;

else
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	ctb_criterio_rateio_item c,
		setor_atendimento b,
		usuario_setor_v a
	where	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	b.cd_estabelecimento_base	= cd_estabelecimento_w
	and	a.nm_usuario		= nm_usuario_p
	and	c.nr_seq_criterio		= nr_seq_crit_rateio_w
	and	c.cd_centro_custo		= b.cd_centro_custo;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
