CREATE OR REPLACE FUNCTION pat_obter_se_ficha_aceite ( cd_estabelecimento_p bigint, nr_seq_bem_p bigint, nr_seq_local_p bigint default null, nr_seq_tipo_p bigint default null) RETURNS varchar AS $body$
DECLARE


cd_empresa_w				bigint;
ie_gerar_w				varchar(01)	:= 'N';
ie_retorno_w				varchar(01)	:= 'N';
nr_seq_grupo_w				bigint;
nr_seq_local_w				bigint;
nr_seq_tipo_w				bigint;
qt_registro_w				bigint;

c01 CURSOR FOR
SELECT	a.ie_gerar
from	pat_regra_ficha_aceite a
where	a.cd_empresa		= cd_empresa_w
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(a.nr_seq_grupo, nr_seq_grupo_w) = nr_seq_grupo_w
and	coalesce(a.nr_seq_tipo, nr_seq_tipo_w) = nr_seq_tipo_w
and	coalesce(a.nr_seq_local, nr_seq_local_w) = nr_seq_local_w
order	by	coalesce(a.nr_seq_local,0),
		coalesce(a.nr_seq_tipo,0),
		coalesce(a.nr_seq_grupo,0);


BEGIN

cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_p);

select	count(*)
into STRICT	qt_registro_w
from	pat_regra_ficha_aceite
where	cd_empresa	= cd_empresa_w;

if (qt_registro_w > 0) then
	begin

	nr_seq_local_w	:= nr_seq_local_p;
	nr_seq_tipo_w	:= nr_seq_tipo_p;

	if (coalesce(nr_seq_local_w::text, '') = '') then
		begin
		select	coalesce(max(nr_seq_tipo),0),
			coalesce(max(nr_seq_local),0)
		into STRICT	nr_seq_tipo_w,
			nr_seq_local_w
		from	pat_bem
		where	nr_sequencia	= nr_seq_bem_p;
		end;
	end if;

	select	coalesce(max(nr_seq_grupo),0)
	into STRICT	nr_seq_grupo_w
	from	pat_tipo_bem
	where	nr_sequencia	= nr_seq_tipo_w;

	open C01;
	loop
	fetch C01 into
		ie_gerar_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		ie_retorno_w	:= ie_gerar_w;

		end;
	end loop;
	close C01;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
