CREATE OR REPLACE FUNCTION pat_obter_se_regra_valor ( nr_seq_regra_valor_p pat_regra_valor_conta.nr_sequencia%type, vl_valor_p bigint, dt_data_p timestamp ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);


BEGIN

begin
select  coalesce(max('S'),'N')
into STRICT  ds_retorno_w
from  pat_regra_valor_conta_item a
where a.nr_seq_regra_valor = nr_seq_regra_valor_p
and   vl_valor_p between a.vl_minimo and coalesce(a.vl_maximo,vl_valor_p)
and   dt_data_p between a.dt_inicio and coalesce(a.dt_final,dt_data_p);
exception
    when others then
        ds_retorno_w := 'N';
end;

return ds_retorno_w;
end;
$body$
