CREATE OR REPLACE FUNCTION pat_obter_se_resp_local ( nr_sequencia_p bigint, dt_referencia_p timestamp, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_usuario_w		varchar(10);
dt_referencia_w			timestamp;
ie_responsavel_w			varchar(1) := 'N';
qt_regra_w			bigint;


BEGIN

dt_referencia_w		:= trunc(coalesce(dt_referencia_p,clock_timestamp()),'dd');
cd_pessoa_usuario_w	:= cd_pessoa_fisica_p;

select	count(*)
into STRICT	qt_regra_w
from	pat_local_resp
where	nr_seq_local	= nr_sequencia_p;

if (qt_regra_w > 0) then


	select	count(*)
	into STRICT	qt_regra_w
	from	pat_local_resp
	where	nr_seq_local		= nr_sequencia_p
	and	cd_pessoa_fisica	= cd_pessoa_usuario_w
	and	((dt_inicio_vig <= dt_referencia_w ) and (coalesce(dt_fim_vig,dt_referencia_w) >= dt_referencia_w));

	if (qt_regra_w > 0) then
		ie_responsavel_w	:= 'S';
	end if;
else
	select	count(*)
	into STRICT	qt_regra_w
	from	pat_local
	where	nr_sequencia		= nr_sequencia_p
	and	cd_pf_resp_local	= cd_pessoa_usuario_w;

	if (qt_regra_w > 0) then
		ie_responsavel_w	:= 'S';
	end if;
end if;

return	ie_responsavel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
