CREATE OR REPLACE FUNCTION pat_obter_taxa_vida_util (nr_seq_bem_p bigint) RETURNS bigint AS $body$
DECLARE


tx_vida_util_w		pat_bem.tx_deprec%Type := 0;
qt_tempo_vida_util_w	pat_bem.tx_deprec%type;


BEGIN

if (nr_seq_bem_p IS NOT NULL AND nr_seq_bem_p::text <> '') then
	select	coalesce(dividir(qt_tempo_vida_util, 12),0)
	into STRICT	qt_tempo_vida_util_w
	from	pat_bem
	where	nr_sequencia = nr_seq_bem_p;
	
	if (qt_tempo_vida_util_w > 0) then
		tx_vida_util_w := dividir(100, qt_tempo_vida_util_w);
	end if;
	
end if;
return	tx_vida_util_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
